import { useEffect, useState } from "react";
import { api } from "../api/client";
import { EstimateBuilder } from "../components/EstimateBuilder";

export function Estimates() {
  const [products, setProducts] = useState([]);
  const [estimates, setEstimates] = useState([]);
  const [msg, setMsg] = useState("");

  async function loadAll() {
    const [p, e] = await Promise.all([
      api.get("/products"),
      api.get("/estimates"),
    ]);

    setProducts(p.data.filter((x) => x.isActive));
    setEstimates(e.data);
  }

  useEffect(() => {
    loadAll();
  }, []);

  async function createEstimate(payload) {
    try {
      const { data } = await api.post("/estimates", payload);
      setMsg(`Presupuesto creado (#${data.folio}) âœ…`);
      await loadAll();

      // Descarga directa (attachment)
      window.location.href = `http://localhost:3001/estimates/${data.id}/pdf`;
    } catch (e) {
      const apiMsg = e?.response?.data?.message;
      const apiErr = e?.response?.data?.error;   // ðŸ‘ˆ ESTO
      setMsg(`Error: ${apiMsg ?? "No se pudo crear presupuesto"}${apiErr ? ` â†’ ${apiErr}` : ""}`);
    }
  }

  return (
    <div className="grid gap-6">
      <div className="rounded-2xl border border-slate-200 bg-white p-4">
        <div className="flex items-center justify-between">
          <div className="font-semibold">Crear presupuesto</div>
        </div>

        <div className="mt-3">
          <EstimateBuilder products={products} onCreate={createEstimate} />
        </div>

        {msg && <div className="mt-2 text-sm text-slate-700">{msg}</div>}
      </div>

      <div className="rounded-2xl border border-slate-200 bg-white p-4">
        <div className="font-semibold mb-3">Presupuestos recientes</div>
        <div className="overflow-auto">
          <table className="min-w-full text-sm">
            <thead className="text-slate-600">
              <tr>
                <th className="text-left py-2">Folio</th>
                <th className="text-left">Cliente</th>
                <th className="text-left">VehÃ­culo</th>
                <th className="text-right">PDF</th>
              </tr>
            </thead>
            <tbody>
              {estimates.map((est) => (
                <tr key={est.id} className="border-t border-slate-100">
                  <td className="py-2 font-semibold">#{est.folio}</td>
                  <td>{est.customerName}</td>
                  <td>
                    {est.vehicleName}
                    {est.vehicleYear ? ` (${est.vehicleYear})` : ""}
                  </td>
                  <td className="text-right">
                    <a
                      className="text-slate-900 underline"
                      href={`http://localhost:3001/estimates/${est.id}/pdf`}
                    >
                      Descargar PDF
                    </a>
                  </td>
                </tr>
              ))}

              {estimates.length === 0 && (
                <tr>
                  <td className="py-6 text-slate-600" colSpan={4}>
                    AÃºn no hay presupuestos.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
